css:
/*Retirar top bar*/
@media (min-width: 1380px){
  :root {
    --max-width-header: 150rem;
  }

  .header__logo {
        max-height: 43px;
        max-width: 241px;
   }

  .omnisend-form-6256d481a363fb0d927adb99-teaser-btn-wrapper {
    border: solid 2px #802E34;
  }


  /font h1/
  .h1, h1 {
      font-size: 2.0rem;
  }

  .header__logo-block {
    padding: 0;
    margin-right: 10px;
  }

  .header__nav-block {
    width: 785px;
  }

  .header .header__nav {
    width: 785px;
    display: flex;
    margin-right: 100px;
  }

  .d-inline-block a {
    font-size: 14px;
  }

  .header {
    display: flex;
    justify-content: center;
  }

  .header__container .container-fluid {
    display: flex;
    padding: 0;
    margin: 0;
    max-width: var(--max-width-header);
  }

  .header__container {
    display: flex;
    max-width: var(--max-width-header);
    padding: 0;
    justify-content: center;
  }

  .header__row {
    display: flex;
    margin: 0;
    max-width: var(--max-width-header);
    justify-content: center;
  }

  .header__nav-block {
    order: 0;
    margin-right: 100px;
  }

  #search-bar {
    order: 1;
  }

  .header__nav-block {
    width: 870px;
  }

  .header .header__row .row {
    display: flex;
    justify-content: center;
  }

  .header .row {
    margin: 20px 0 20px 0;
  }

  .header form {
    width: 227px;
    margin-right: 5px;
    margin-left: 5px;
    display: flex;
    align-items: center
    justify-content: center;
  }

  .header .header__container .col-12 {
    width: 750px;
    padding: 0;
    flex: 0;
    display: flex;
  }
}


/*font h1*/
.h1, h1 {
    font-size: 2.0rem;
}

@media (min-width: 768px){
    .top-bar__nav {
      display: none!important;
    }

    .top-bar__countdown {
      font-size: 14px;
    }

    .header__nav {
      font-size: 16px;
     }

    .header__row {
      margin-top: 25px;
    }

    .header__nav a {
      font-weight: bold;
    }

    .menu__item {
      font-weight: bold;
    }

    .product-card__name {
      font-size: 16px;
      height: 70px;
    }

    .prices__value {
      font-size: 20px;
    }

    #header {
        display: block !important;
        background: #FAEDDA;
        background-size: auto auto;
        color: #6f6057;
        background-size: 100%;
    }

    .header__search-input {
      border: 1px solid #802E34;
    }

    /*tamanho preço frete*/
    .ml-2, .mx-2 {
      font-size: 14px;
      font-weight: bold;
    }

    /*tamanho descrição frete*/
    .small, small {
      font-size: 100%;
      font-weight: bold;
    }

    /*cor texto listagem frete*/
    .list-group-item {
      color: #802E34;
    }

    .list-group-item:hover {
      color: #802E34;
    }

    /*cor background listagem frete e borda
    .list-group-item {
      background-color: #FFEFD1;
      border: 1px solid #802E34;
    }*/

    /*Texto negrito botao comprar*/
    .btn {
      font-weight: bold;
      border-radius: 5px;
    }

    /*Texto maiusculo botao comprar*/
    .btn-group-lg>.btn, .btn-lg {
      text-transform: uppercase;
      padding-top: 15px;
      padding-left: 40px;
      padding-right: 40px;
      padding-bottom: 15px
    }

    .lead {
      font-size: 32px;
      font-weight: 300;
    }

    .container, .container-lg, .container-md, .container-sm {
      font-size: 17px;
    }

    .products-carousel__title {
      font-size: 20px;
    }

    /*botão comprar fixo*/
    .product-card__buy.fade {
      opacity: 1!important;
      padding-top: 10px;
    }

    .footer__title {
      font-size: 22px;
    }

    .product-card:hover  {
      border: 1px solid #802E34;
    }

    .input-group-append .btn, .input-group-prepend .btn {
      border-radius: 0px;
    }

    .shipping-calculator__services:hover{
      cursor: auto;      
    }

    .cart .cart-item__name {
      font-size: 20px;
    }

    .cart-item__prices {
      font-weight: bold;
    }

    .footer {
      display: block !important;
       background: #f5f5f5 url("https://sequilhato.com.br/wp-content/themes/sequilhato/img/bg-home-produto.jpg");
       background-size: auto auto;
       color: #6f6057;
       background-size: 100%;
    }

    .footer__links {
      font-size: 90%;
    }

    .list-group-item.active:hover {
      background-color: #a1a1a1;
      color: white;
    }

   .celltronics-deskonly {
        /*background-color: #FCBF49 0% 0% no-repeat padding-box;*/
        background-color: #FCBF49;
        border-radius: 5px;
        letter-spacing: 0px;
        color: #812E34;
        text-transform: uppercase;
        padding: 10px 110px;
    }
}

@media  screen and (min-width: 992px) and (max-width: 1367px) {
  .banner-slider {
    max-width: 100% !important;
  }
}
// apenas tablet
@media screen and (min-width: 481px) and (max-width: 991px) {
  .banner-slider {
    max-width: 100% !important;
  }
}

/*Cor cabeçalho*/
.header {
    background: #ffffff;
}

