@media (min-width: 992px){

  .top-bar__countdown {
    font-size: 12px;
    padding-top: var(--spacer-1);
    padding-bottom: var(--spacer-1);
  }
}

.shipping-calculator__services {
    font-size: 11px !important;
}

.shipping-calculator__services:before {
    color: #333;
    content: "Desconto na 1ª compra com o cupom PRIMEIRACOMPRA";
    font-size: 12px;
    font-weight: 500;
    white-space: pre;
    background: #f6e7ba;
    padding: 5px;
    border-radius: 5px;
    font-weight: bold;
width: 100%;
}
.product__buy:after {
    color: #ffffff;
    content: "Valor mínimo de R$80 para compras no site";
    font-size: 12px;
    font-weight: 500;
    white-space: pre;
    background: #000000;
    padding: 5px;
    font-weight: bold;
    margin-top: 10px;
}

@media (max-width: 767px){
  /*botao na pesquisa*/
.search-engine__retail .product-card__buy button {
     margin-top: -2.7rem!important;
    margin-right: 2.3rem!important;
}
   /*limitação de linhas de texto na pesquisa*/
  .product-card--small .product-card__name {
    line-height: 33px;

.header__submenu {
     margin-top: 6px;
}

}
}

.product__free-shipping-from {
display: none;


  /* --- Adiciona aviso de prazo de envio abaixo do frete --- */
.shipping-calculator__services:after {
  content: "Os pedidos serão enviados em até 3 dias após a confirmação do pagamento.";
  display: block;        /* Garante que o texto fique em uma linha própria */
  text-align: center;    /* Centraliza o aviso */
  font-size: 13px;       /* Tamanho da fonte (ajuste se precisar) */
  font-weight: bold;   /* Coloca em negrito */
  color: #333;          /* Cor do texto (cinza escuro) */
  margin-top: 15px;      /* Espaço para separar da lista de fretes */
  margin-bottom: 10px;   /* Espaço antes da barra de "frete grátis" */
}
