css:
/*Retirar top bar*/

/*botão cancelar*/
.btn-danger {
    display: none !important;
}

/*font h1*/
.h1, h1 {
    font-size: 2.0rem;
}

@media (min-width: 768px){
    .top-bar__nav {
      display: none!important;
    }

    .top-bar__countdown {
      background: #802e34;
      color: #ffffff;
      font-size: 14px;
    }

    .header__nav {
      font-size: 16px;
     }

    .header__row {
      margin-top: 25px;
    }

    .header__nav a {
      font-weight: bold;
    }

    .menu__item {
      font-weight: bold;
    }

    .product-card__name {
      font-size: 16px;
    }

    .prices__value {
      font-size: 20px;
    }

    #header {
      display: block!important;
      background: #fff url(https://www.sequilhato.com.br/wp-content/themes/sequilhato/img/bg-home-produto.jpg);
      background-size: auto auto;
      color: #6f6057;
      background-size: 100%;
    }

    .header__search-input {
      border: 1px solid #802E34;
    }

    /*tamanho preço frete*/
    .ml-2, .mx-2 {
      font-size: 14px;
      font-weight: bold;
    }

    /*tamanho descrição frete*/
    .small, small {
      font-size: 100%;
      font-weight: bold;
    }

    /*cor texto listagem frete*/
    .list-group-item {
      color: #802E34;
    }

    .list-group-item:hover {
      color: #802E34;
    }

    /*cor background listagem frete e borda
    .list-group-item {
      background-color: #FFEFD1;
      border: 1px solid #802E34;
    }*/

    /*Texto negrito botao comprar*/
    .btn {
      font-weight: bold;
      border-radius: 30px;
    }

    /*Texto maiusculo botao comprar*/
    .btn-group-lg>.btn, .btn-lg {
      text-transform: uppercase;
      padding-top: 15px;
      padding-left: 40px;
      padding-right: 40px;
      padding-bottom: 15px
    }

    .lead {
      font-size: 32px;
      font-weight: 300;
    }

    .container, .container-lg, .container-md, .container-sm {
      font-size: 17px;
    }

    .products-carousel__title {
      font-size: 20px;
    }

    /*botão comprar fixo*/
    .product-card__buy.fade {
      opacity: 1!important;
      padding-top: 10px;
    }

    .footer__title {
      font-size: 22px;
    }

    .product-card:hover  {
      border: 1px solid #802E34;
    }

    .input-group-append .btn, .input-group-prepend .btn {
      border-radius: 0px;
    }

    .shipping-calculator__services:hover{
      cursor: auto;
      background: #A17245;
    }

    .cart .cart-item__name {
      font-size: 20px;
    }

    .cart-item__prices {
      font-weight: bold;
    }

    .footer {
      background: #FAEDDA;
    }

    .footer__links {
      font-size: 90%;
    }

    .list-group-item.active:hover {
      background-color: #a1a1a1;
      color: white;
    }
}

@media  screen and (min-width: 992px) and (max-width: 1367px) {
  .banner-slider {
    max-width: 100% !important;
  }
}
// apenas tablet
@media screen and (min-width: 481px) and (max-width: 991px) {
  .banner-slider {
    max-width: 100% !important;
  }
}

