css:
/*Retirar top bar*/
  .top-bar__nav {
    max-width: 0;
  }


@media (min-width: 768px){
    .d-md-block { 
      display: none!important;
     }

    .header__nav {
      font-size: 16px;
     }

    .header__row {
      margin-top: 20px;
    }

    .header__nav a {
      font-weight: bold;
    }

    .menu__item {
      font-weight: bold;
    }

    .product-card__name {
      font-size: 16px;
    }

    .prices__value {
      font-size: 20px;
    }

    #header {
      display: block!important;
      background: #fff url(https://www.sequilhato.com.br/wp-content/themes/sequilhato/img/bg-home-produto.jpg);
      background-size: auto auto;
      color: #6f6057;
      background-size: 100%;
    }

    .header__search-input {
      border: 1px solid #802E34;
    }

    /*tamanho preço frete*/
    .ml-2, .mx-2 {
      font-size: 14px;
      font-weight: bold;
    }

    /*tamanho descrição frete*/
    .small, small {
      font-size: 100%;
      font-weight: bold;
    }

    /*cor texto listagem frete*/
    .list-group-item {
      color: #802E34;
    }

    .list-group-item:hover {
      color: #802E34;
    }

    /*cor background listagem frete e borda*/
    .list-group-item {
      background-color: #FFEFD1;
      border: 1px solid #802E34;
    }

    /*Texto negrito botao comprar*/
    .btn {
      font-weight: bold;
    }

    /*Texto maiusculo botao comprar*/
    .btn-group-lg>.btn, .btn-lg {
      text-transform: uppercase;
    }

    .lead {
      font-size: 32px;
      font-weight: 300;
    }

    .container, .container-lg, .container-md, .container-sm {
      font-size: 17px;
    }

    .h4{
      font-size: 36px;
    }
}

/*Cor cabeçalho*/
.header {
    background: #ffffff;
}

@font-face {
  font-family: "Symbol Book SSi";
  src: url("/symbol-book-ssi-book.ttf");
}

body {
    font-family: Symbol Book SSi;
}
